// function bind(fn, scope) {
//     return function (...args) {
//         fn.apply(scope, args);
//     }
// }

class RowsView {
    constructor(rowCount) {
        this.dom = document.querySelector(".rows");
        this.rows = []; 
        this.index = 0;
        
        for (let x = 1; x <= rowCount; x++) {
            const rowView = new RowView(x, this._next.bind(this));
            this.dom.appendChild(rowView.dom);
            this.rows.push(rowView);
        }
        this._next();
    }
    _next() {
        this.rows[this.index].setActive(true);
        this.index += 1;
    }
}

class RowView {
    constructor(number, done) {
        this.dom = createElement("div", "row");
        const numberLine = createElement("div", "numbers", number);
        const buttonBlock = createElement("div", "button_block");
        const button = createElement("button", undefined, "OK");
        button.addEventListener("click", () => {
            this.setActive(false);    
            done();
        });

        this.active = false;
        const guessesView = new GuessesView(this);
        const answersView = new AnswersView();

        appendChildren(this.dom, numberLine, guessesView.dom, buttonBlock);
        appendChildren(buttonBlock, answersView.dom, button);

        this.setActive(false);
    }
    setActive(value) {

        // if (value) { 
        //     this.dom.classList.remove("inactive"); 
        // } else {
        //     this.dom.classList.add("inactive");
        // }
        
        this.dom.classList.toggle("inactive", !value);
        this.active = value;
    }
}

const COLORS = ["red", "blue", "green", "yellow", "orange"];

class GuessesView {
    constructor(rowView) {
        const code = [];

        this.dom = createElement("ul", "guesses");
        const hints = createHints(this.dom);
        hints.forEach((hint, index) => {
        let x = 0;    
            hint.addEventListener("click", () => {
                if (rowView.active) { 
                    code[index] = x;
                    hint.style.background = COLORS[x];
                    x = (x + 1) % 5;
                }
            });
        });
    }
}

class AnswersView {
    constructor() {
        this.dom = createElement("ul", "answers");
        createHints(this.dom);        
    }
}

class Game {
    constructor() {
        let hiddenCode = [];

        while (hiddenCode.length < 5) {
            hiddenCode.push(Math.floor(Math.random() * 5));
        } 

        const rowsView = new RowsView(10);
        
        
    }
}



const hola = new Game();

////////// Functions ///////////
function createHints(dom) {
    const hints = [];
    for (let x = 0; x < 5; x++) {
        const hint = createElement("li", "hint"); 
        dom.appendChild(hint);
        hints.push(hint);  
    }
    return hints;
}

function createElement(tag, className, text) {
    const element = document.createElement(tag);
    if (className) {
        element.classList.add(className);
    }
    if (text) {
        element.innerText = text;
    }
    return element;
}

function appendChildren(dom, ...children) {
    children.forEach(child => {
        dom.appendChild(child);
    });
}
